export default class TableStore {
    key: string;
    tableId: string;
    constructor(opts: {
        $router: any;
        tableName: string;
        keyType: string | boolean;
    });
    getTableId(name: string): string;
    getTable(): any;
    saveTable(table: any): void;
    clearTable(): void;
    updateTableValue(value: any, key?: string): void;
    getItemKey($route: any, keyType: any): string;
    getTableValue(key?: string): any;
    clearTableValue(key?: string): void;
}
